const lastUpdate="2021 sep 14 19:00",daysEl=document.getElementById("days"),hoursEl=document.getElementById("hours"),minsEl=document.getElementById("mins"),secondsEl=document.getElementById("seconds");function count(){const e=new Date(lastUpdate),n=(new Date-e)/1e3,t=Math.floor(n/3600/24),o=Math.floor(n/3600)%24,s=Math.floor(n/60)%60,l=Math.floor(n%60);daysEl.innerHTML=t,hoursEl.innerHTML=o,minsEl.innerHTML=s,secondsEl.innerHTML=l}count(),setInterval(count,1e3);